import React from 'react';
import { AiOutlinePercentage } from 'react-icons/ai';
import { TiDownloadOutline } from 'react-icons/ti';
import { FiDollarSign } from 'react-icons/fi';
import { Button } from 'react-bootstrap';
import { useMediaQuery } from 'react-responsive';

import './EmployeeInfoForm.css';
import '././PaySlipDetails/PaySlipDetails.js';


class EmployeeInfoForm extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
    firstname: '',
    lastname:'',
    annualsalary: '',
    superrate: '',
    payDate: '',
    payFrequency: '',
    grossIncome: '',
    incomeTax:'',
    netIncome:'',
    superValue:'',
    payValue:''
  };
  }

  handleChange = (e) => {
  this.setState({
    firstname: e.target.value,
    lastname: e.target.value,
    annualsalary: e.target.value,
    superrate: e.target.value
  })
}

generatePayslip= () =>{

  let incomeTax;
  const fullName = this.state.this.state.firstname + " " + this.state.lastname;
  const annualSalary = this.state.annualsalary;
  const employeeSuper = this.state.superrate;


    if (annualSalary <= 18200){
    	incomeTax = 0;
    }else if(annualSalary > 18200 && annualSalary <= 37000){
    	incomeTax = Math.round(((annualSalary - 18200)* (19/100)));
    }else if(annualSalary > 37000 && annualSalary <= 80000){
    	incomeTax = Math.round( 3572 + ((annualSalary - 37000)* (325/100)));
    }else if(annualSalary > 80000 && annualSalary <= 180000){
    	incomeTax = Math.round( 17547 + ((annualSalary - 80000)* (37/100)));
    }else if(annualSalary > 180000){
    	incomeTax = Math.round( 54547 + ((annualSalary - 180000)* (45/100)));
    }
    const grossIncome =  Math.round(annualSalary/12);
    const netIncome = grossIncome - incomeTax;
    const superValue = Math.round(grossIncome*employeeSuper/100);
    const payValue = netIncome - superValue;

    this.setState({
      payDate: new Date().getDate(),
      payFrequency: 'Monthly',
      grossIncome: grossIncome,
      incomeTax: incomeTax,
      netIncome: netIncome,
      superValue: superValue,
      payValue:payValue
      })
}
  render() {
    return (

      <div className="emplyoeewrapper">
        <div className="container">
      <form className="employee-info-form">
        <h2>Employee Info </h2>
          <div className="name">
                  <input type = "text" className="firstname" name = "firstname" placeholder = "Firstname" onChange={this.changeEvent}/>
                  <span className="firstnameicon"> <TiDownloadOutline /> </span>

              <input type = "text" className="lastname" name = "lastname" placeholder = "Lastname" onChange={this.changeEvent}/>
          </div>
          <div className="salary">
            <span className="dollaricon"> <FiDollarSign /> </span>
            <input type="number" min="0" className="annualsalary" name="annualsalary" placeholder = "Annual Salary" onChange={this.changeEvent}/>
            <span className="decimalicon"> .00 </span>
            <span className="percentageicon"> <AiOutlinePercentage /> </span>
            <input type="number" min="0" className="superrate" name="superrate" placeholder = "Super Rate" onChange={this.changeEvent}/>

        </div>
        <div className="generatebutton" onClick={() =>"this.generatePayslip"}>
          <Button variant='primary'>Generate Payslip</Button>
          </div>
          <div>
          <PaySlipDetails val={this.state} />
      </div>
      </form>
      </div>
      </div>
    );
  }
}

export default EmployeeInfoForm;
