import React from 'react';
import './EmployeeInfoForm.css';
import {Button, FormGroup} from 'react-bootstrap';
import { useMediaQuery } from 'react-responsive';

class EmployeeInfoForm extends React.Component {

  constructor(props) {
    super(props);
    this.state = {};
  }

  render() {
      const isDesktopOrLaptop = useMediaQuery({ query: '(min-device-width: 1224px)' });
      const isBigScreen = useMediaQuery({ query: '(min-device-width: 1824px)' });
      const isTabletOrMobile = useMediaQuery({ query: '(max-width: 1224px)' });
      const isTabletOrMobileDevice = useMediaQuery({ query: '(max-device-width: 1224px)' });
    return (

      <div className="wrapper">
        <div className="container">
      <form className="employee-info-form">
        <h2>Employee Info </h2>
        <FormGroup>
          <div className="name">
            <input type = "text" className="firstname" id = "firstname" placeholder = "Firstname" />
            <input type = "text" className="lastname" id = "lastname" placeholder = "Lastname"/>
          </div>
          <div className="salary">
            <input type="number" min="0" className="annualsalary" id="annualsalary" placeholder = "Annual Salary"  />
            <input type="number" min="0" className="superrate" id="superrate" placeholder = "Super Rate" />

        </div>
        </FormGroup>
        <div className="button">
          <Button variant='primary'>Generate Payslip</Button>
          </div>
      </form>
      </div>
      </div>
    );
  }
}

export default EmployeeInfoForm;
